mapper.select:
SELECT B.IBOARD, B.ICODE, C.NAME AS BOARD_NAME, B.TITLE, B.SEC_YN, B.DEL_YN, DATE_FORMAT(B.CREATED_AT, '%Y-%m-%d') AS CREATED_AT FROM TBL_BOARD B INNER JOIN TBL_CODE C ON B.ICODE = C.ICODE
B.ICODE = #{icode}
AND DEL_YN = 'N'
ORDER BY B.CREATED_AT DESC LIMIT #{offset}, #{amount}

mapper.delete:
UPDATE TBL_BOARD
SET DEL_YN = 'Y'
WHERE IBOARD = #{iboard}

mapper.select:
SELECT COUNT(*) FROM TBL_BOARD
ICODE = #{icode}
AND DEL_YN = 'N'

mapper.update:
INSERT INTO TBL_THUMBNAIL(IBOARD, NAME) VALUES(
	#{iboard},
	#{name}
)
ON DUPLICATE KEY UPDATE
NAME = VALUES(NAME)
